CREATE DATABASE IF NOT EXISTS pcc;
USE pcc;
CREATE TABLE IF NOT EXISTS FASE(
		id  INT (5) NOT NULL PRIMARY KEY AUTO_INCREMENT,
		tema TEXT NOT NULL,
		nivel INT (1) NOT NULL
);

CREATE TABLE IF NOT EXISTS FASES(
		id_fase INT (5) NOT NULL,
		tema TEXT NOT NULL,
		conteudo TEXT NOT NULL,
		tipos CHAR (6),
		links TEXT NOT NULL,
		CONSTRAINT FOREIGN KEY (id_fase) REFERENCES FASE (id)
		
);

CREATE TABLE IF NOT EXISTS perguntas(
		id INT (5) NOT NULL PRIMARY KEY AUTO_INCREMENT,
		nivel TEXT  NOT NULL,
		titles TEXT  NOT NULL,
		medias TEXT NOT NULL,
		texts TEXT  NOT NULL,
		respostas TEXT NOT NULL,
		dicas TEXT  NOT NULL,
		links TEXT not NULL,
		pontos TEXT NOT NULL
);

CREATE TABLE IF NOT EXISTS raking(
	nickname TEXT NOT NULL,
	nivel INT (5) NOT NULL,
	tipo TEXT NOT NULL,
	fase INT (10) NOT NULL,
	pontos BIGINT (30) NOT NULL
);

CREATE TABLE IF NOT EXISTS roteiros (
		id INT (5) NOT NULL PRIMARY KEY AUTO_INCREMENT,
		nivel TEXT  NOT NULL,
		titles TEXT  NOT NULL,
		strutures TEXT NOT NULL,
		medias TEXT NOT NULL,
		texts TEXT  NOT NULL,
		respostas TEXT NOT NULL,
		pontos TEXT NOT NULL,
		dicas TEXT  NOT NULL,
		links TEXT not NULL
);

